import React, { useEffect, useState } from "react";
import {
  fetchSnippets,
  createSnippet,
  updateSnippet,
  deleteSnippet,
} from "./apiService"; // Assuming existing functions
import "../styles.css"; // Optional styling file

const SnippetListPage = () => {
  const [snippets, setSnippets] = useState([]);
  const [error, setError] = useState(null);

  // Fetch snippets on component mount
  useEffect(() => {
    const getSnippets = async () => {
      try {
        const data = await fetchSnippets();
        setSnippets(data);
      } catch (err) {
        setError(err.message);
      }
    };
    getSnippets();
  }, []);

  const handleAddSnippet = async () => {
    const newSnippet = {
      title: "New Snippet",
      description: 'Javascript snippet',
      code: 'console.log("Hello World!");',
      language: "javascript",
    };
    try {
      const addedSnippet = await createSnippet(newSnippet);
      setSnippets([...snippets, addedSnippet]);
    } catch (err) {
      console.error("Error adding snippet:", err);
    }
  };

  const handleUpdateSnippet = async (snippetId) => {
    const updatedData = {
      title: "Updated Snippet",
      description: "Snippet Update",
      code: 'console.log("Updated Code!")',
      language:'Javascript'
    };
    try {
      const updatedSnippet = await updateSnippet(snippetId, updatedData);
      setSnippets(
        snippets.map((snippet) =>
          snippet.id === snippetId ? updatedSnippet : snippet
        )
      );
    } catch (err) {
      console.error("Error updating snippet:", err);
    }
  };

  const handleDeleteSnippet = async (snippetId) => {
    try {
      await deleteSnippet(snippetId);
      setSnippets(snippets.filter((snippet) => snippet.id !== snippetId));
    } catch (err) {
      console.error("Error deleting snippet:", err);
    }
  };

  const handleGenerateTags = async (snippetId) => {
    try {
      // Call backend API to generate tags for the given snippet ID
      const response = await fetch(
        `/api/snippets/${snippetId}/generate-tags/`,
        {
          method: "POST",
          headers: {
            "Content-Type": "application/json",
            Authorization: `Basic ${btoa("orban:orban")}`, // Basic Auth
          },
        }
      );

      if (!response.ok) {
        throw new Error(`Failed to generate tags: ${response.statusText}`);
      }

      const updatedSnippet = await response.json();
      // Update snippet list with the newly generated tags
      setSnippets(
        snippets.map((snippet) =>
          snippet.id === snippetId ? updatedSnippet : snippet
        )
      );
    } catch (err) {
      console.error("Error generating tags:", err);
    }
  };

  return (
    <div>
      <h1>Snippet List</h1>
      {error && <p style={{ color: "red" }}>Error: {error}</p>}
      <button className='btn btn-primary' onClick={handleAddSnippet}>
        Add Snippet
      </button>
      <table border='1'>
        <thead>
          <tr>
            <th>ID</th>
            <th>Title</th>
            <th>Description</th>
            <th>Code</th>
            <th>Language</th>
            <th>Tags</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {snippets.map((snippet) => (
            <tr key={snippet.id}>
              <td>{snippet.id}</td>
              <td>{snippet.title}</td>
              <td>{snippet.description}</td>
              <td>{snippet.language}</td>
              <td>
                <pre>{snippet.code}</pre>
              </td>
              <td>{snippet.tags ? snippet.tags.join(", ") : "No tags"}</td>
              <td>
                <button class onClick={() => handleUpdateSnippet(snippet.id)}>
                  Update
                </button>
                <button onClick={() => handleDeleteSnippet(snippet.id)}>
                  Delete
                </button>
                <button onClick={() => handleGenerateTags(snippet.id)}>
                  Generate Tags
                </button>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
};

export default SnippetListPage;
