import React, { useState, useEffect } from "react";
import API from "../Api";
import { Link } from "react-router-dom";
import "../styles.css";

function SnippetList() {
  const [snippets, setSnippets] = useState([]);

  useEffect(() => {
    API.get("/snippets/")
      .then((response) => setSnippets(response.data))
      .catch((error) => console.error("Error fetching snippets:", error));
  }, []);

  const deleteSnippet = (id) => {
    API.delete(`/snippets/${id}`)
      .then(() => setSnippets(snippets.filter((snippet) => snippet.id !== id)))
      .catch((error) => console.error("Error deleting snippet:", error));
  };

  return (
    <div className='snippet-list'>
      <h2>Snippet List</h2>
      <table>
        <thead>
          <tr>
            <th>Title</th>
            <th>Description</th>
            <th>Actions</th>
          </tr>
        </thead>
        <tbody>
          {snippets.map((snippet) => (
            <tr key={snippet.id}>
              <td>{snippet.title}</td>
              <td>{snippet.description}</td>
              <td>{snippet.description}</td>
              <td>
                <button onClick={() => deleteSnippet(snippet.id)}>
                  Delete
                </button>
                <Link to={`/add-snippet?id=${snippet.id}`}>Update</Link>
              </td>
            </tr>
          ))}
        </tbody>
      </table>
    </div>
  );
}

export default SnippetList;
